*Zur Info: KHbbmyo12 entspricht EHIS Varname CD1C;
IF KHmyo12 < 1 or BBmyo12 < 1 THEN KHbbmyo12=.;
IF KHmyo12=1 or BBmyo12=1 THEN KHbbmyo12=1; /*Ja*/
IF KHmyo12=2 and BBmyo12=2 THEN KHbbmyo12=2; /*Nein*/

*Zur Info: KHbbsa12 entspricht EHIS Varname CD1F;
IF KHsa12 < 1 or BBsa12 < 1 THEN KHbbsa12=.;
IF KHsa12=1 or BBsa12=1 THEN KHbbsa12=1; /*Ja*/
IF KHsa12=2 and BBsa12=2 THEN KHbbsa12=2; /*Nein*/

/*"Einschränkung durch Krankheit seit mind. 6 Monaten"*/
*GZmehm2D entspricht EHIS-Var HS3;
IF GZmehm2D_h1 =1 AND GZmehm2D_h2 =2 THEN GZmehm2D=1;/*stark eingeschränkt/severely limited*/ 
IF GZmehm2D_h1 =2 AND GZmehm2D_h2 =2 then GZmehm2D=2;/*mäßig eingeschränkt/limited but not severely*/ 
IF GZmehm2D_h1 =3 THEN GZmehm2D=3;/*nicht eingeschränkt/not limited at all*/ 
IF GZmehm2D_h1 in (1 2) AND GZmehm2D_h2 =1 then GZmehm2D=3;/*nicht eingeschränkt/not limited at all*/

/*Recoding of vision and hearing difficulty variables*/
 /*SH und BB betreffen!*/
IF SHseh1=3 THEN PL2p=4; ELSE PL2P=SHseh2C;/*blind persons*/
IF SHhoer1=3 THEN PL4p=4; ELSE PL4p=SHhoer5;/*deaf persons*/
IF SHhoer1=3 OR SHhoer5=4 THEN PL5p=4; ELSE PL5p=SHhoer6;/*deaf persons*/

/*Calculation of a summary variable, PLp= max(PL2p, PL4p, PL5p, PL6, PL7);*/
PLp= max(PL2p, PL4p, PL5p, Bbgehen, Bbtreppe);

/*Calculation of a recoded variable for dissemination(Verteilung): PLc*/
IF PLp=1 THEN Shbblimit_k=1;/*None/Keine*/
IF PLp=2 THEN Shbblimit_k=2;/*Moderate/Mittelgradig*/
IF PLp=3 OR PLp=4 THEN Shbblimit_k=3;/*Severe/Schwerwiegend*/

 *RKI-EHIS-Namen:;
 *SHseh1 entspricht EHIS PL1;
 *SHseh2C entspricht EHIS PL2;
 *SHshoer1 entspricht EHIS PL3;
 *SHshoer5 entspricht EHIS PL4;
 *SHshoer6 entspricht EHIS PL5;
*S Hbblimit_k entspricht EHIS PLc;

/*Creating a recoded variable for dissemination(Verteilung): PL2c*/
IF PL2p=1 THEN PL2c=1;/*None/Keine*/
IF PL2p=2 THEN PL2c=2;/*Moderate/Mittelgradig*/
IF PL2p=3 OR PL2p=4 THEN PL2c=3;/*Severe/Schwerwiegend*/

SHsehlimit_k=PL2c;

/*Calculation of a summary variable, PLp2= max(PL2p, PL4p, PL5p, PL6, PL7, PL8);*/
/*SH und BB betreffend!*/
/*Zwischenvariablen PL2p, PL4p, PL5p wurden bereits bei der Berechnung von SHbblimit_k generiert*/
PLp2= max(PL2p, PL4p, PL5p, BBgehen, BBtreppe, BBkonz1);

/*Calculation of a recoded variable for dissemination(Verteilung): PLc2*/
IF PLp2=1 THEN SHbblimit2_k=1;/*None/Keine*/
IF PLp2=2 THEN SHbblimit2_k=2;/*Moderate/Mittelgradig*/
IF PLp2=3 OR PLp2=4 THEN SHbblimit2_k=3;/*Severe/Schwerwiegend*/

*SHbblimit2_k entspricht EHIS PLc2;

/*Calculation of a total hearing difficulty variable: PL45/Shhoerlimit_45*/
 /*Zwischenvariablen PL4p, PL5p wurden bereits bei der Berechnung von Shbblimit_k generiert*/
 PL45= max(PL4p, PL5p);
 /*Recoding the total hearing difficulty variable: PL45*/
 IF PL45=1 THEN Shhoerlimit_k=1;/*None/Keine*/
 IF PL45=2 THEN Shhoerlimit_k=2;/*Moderate/Mittelgradig*/
 IF PL45=3 OR PL45=4 THEN Shhoerlimit_k=3;/*Severe/Schwerwiegend*/
 *Shhoerlimit_k entspricht EHIS PL45;

/*Creating a new variable: PL_CRE*/
 IF SHseh1=1 AND SHseh2C=1 THEN SHsehkorr_k=1;/*Vision difficulty corrected with no remaining eye health problem/Sehschwäche korrigiert, ohne verbleibende Beeinträchtigung der Augengesundheit*/
 IF SHseh1=1 AND SHseh2C in (2 3 4) THEN SHsehkorr_k=2;/*Vision difficulty corrected with remaining eye health problem/Sehschwäche korrigiert mit verbleibendem Augengesundheitsproblem*/
 IF SHseh1=2 AND SHseh2C in (2 3 4) THEN SHsehkorr_k=3;/*Vision difficulty without correction/Sehschwäche ohne Korrektur*/
 IF SHseh1=2 AND SHseh2C=1 THEN SHsehkorr_k=4;/*No vision difficulty/Keine Sehschwierigkeiten*/
 *SHsehkorr_k entspricht EHIS PL_CRE;

SHseh1_k=SHseh1;
if SHseh1_k=3 then SHseh1_k=.;
if SHseh1_k=.K  then SHseh1_k=.;
if SHseh1_k=.W then SHseh1_k=.;

/*Calculation of a total walking difficulty variable: PL67*/
PL67= max(BBgehen, BBtreppe);

/*Recoding the total walking difficulty variable: PL67*/
IF PL67=1 THEN BBgehlimit_k=1;/*None/Keine*/
IF PL67=2 THEN BBgehlimit_k=2;/*Moderate/Mittelgradig*/
IF PL67=3 OR PL67=4 THEN BBgehlimit_k=3;/*Severe/Schwerwiegend*/

/*Creating a recoded variable for dissemination(Verteilung): PL8c*/
IF BBkonz1=1 THEN PL8c=1;/*None/Keine*/
IF BBkonz1=2 THEN PL8c=2;/*Moderate/Mittelgradig*/
IF BBkonz1=3 OR BBkonz1=4 THEN PL8c=3;/*Severe/Schwerwiegend*/

BBkonzlimit_k=PL8c;

/*Creating a recoded variable for dissemination(Verteilung): PL9c*/
IF BBessen=1 THEN PL9c=1;/*None/Keine*/
IF BBessen=2 THEN PL9c=2;/*Moderate/Mittelgradig*/
IF BBessen=3 OR BBessen=4 THEN PL9c=3;/*Severe/Schwerwiegend*/

BBesslimit_k=PL9c;
IF age<55 THEN BBesslimit_k=.N;/*Nicht erhoben Age<55*/

/*Calculation of total variable: PCp*/
PCp= max(BBschwierig1, BBschwierig2, BBschwierig3, BBschwierig4, BBschwierig5);


/*Creating a recoded variable for dissemination(Verteilung)in at least one personal care activity (in mindestens einer Körperpflegetätigkeit): PCc*/
IF PCp=1 THEN PCc=1;/*None/Keine*/
IF PCp=2 THEN PCc=2;/*Moderate/Mittelgradig*/
IF PCp=3 OR PCp=4 THEN PCc=3;/*Severe/Schwerwiegend*/

BBcarelimit_k=PCc;
IF age<55 THEN BBcarelimit_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), A. Difficulty in feeding oneself/Schwierigkeiten bei der Nahrungsaufnahme: PC1Ad*/
IF BBschwierig1=1 THEN PC1Ad=1;/*None/Keine*/
IF BBschwierig1=2 THEN PC1Ad=2;/*Moderate/Mittelgradig*/
IF BBschwierig1=3 OR BBschwierig1=4 THEN PC1Ad=3;/*Severe/Schwerwiegend*/

BBlimitA_k=PC1Ad;
IF age<55 THEN BBlimitA_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), B. Difficulty in getting in and out of a bed or chair/Schwierigkeiten sich von Bett oder Stuhl zu erheben/niederlassen: PC1Bd*/
IF BBschwierig2=1 THEN PC1Bd=1;/*None/Keine*/
IF BBschwierig2=2 THEN PC1Bd=2;/*Moderate/Mittelgradig*/
IF BBschwierig2=3 OR BBschwierig2=4 THEN PC1Bd=3;/*Severe/Schwerwiegend*/

BBlimitB_k=PC1Bd;
IF age<55 THEN BBlimitB_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), C. Difficulty in dressing and undressing/Schwierigkeiten beim An- und Ausziehen: PC1Cd*/
IF BBschwierig3=1 THEN PC1Cd=1;/*None/Keine*/
IF BBschwierig3=2 THEN PC1Cd=2;/*Moderate/Mittelgradig*/
IF BBschwierig3=3 OR BBschwierig3=4 THEN PC1Cd=3;/*Severe/Schwerwiegend*/

BBlimitC_k=PC1Cd;
IF age<55 THEN BBlimitC_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), D. Difficulty in using toilets/Schwierigkeiten bei der Toilettenbenutzung: PC1Dd*/
IF BBschwierig4=1 THEN PC1Dd=1;/*None/Keine*/
IF BBschwierig4=2 THEN PC1Dd=2;/*Moderate/Mittelgradig*/
IF BBschwierig4=3 OR BBschwierig4=4 THEN PC1Dd=3;/*Severe/Schwerwiegend*/

BBlimitD_k=PC1Dd;
IF age<55 THEN BBlimitD_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), E. Difficulty in bathing and showering/Schwierigkeiten beim Baden und Duschen: PC1Ed*/
IF BBschwierig5=1 THEN PC1Ed=1;/*None/Keine*/
IF BBschwierig5=2 THEN PC1Ed=2;/*Moderate/Mittelgradig*/
IF BBschwierig5=3 OR BBschwierig5=4 THEN PC1Ed=3;/*Severe/Schwerwiegend*/

BBlimitE_k=PC1Ed;
IF age<55 THEN BBlimitE_k=.N;/*Nicht erhoben Age<55*/

/*Calculation of derived variables*/
IF BBhh1 in (1 2 3 4) THEN HA1Ap=BBhh1; ELSE HA1Ap=.;
IF BBhh2 in (1 2 3 4) THEN HA1Bp=BBhh2; ELSE HA1Bp=.;
IF BBhh3 in (1 2 3 4) THEN HA1Cp=BBhh3; ELSE HA1Cp=.;
IF BBhh4 in (1 2 3 4) THEN HA1Dp=BBhh4; ELSE HA1Dp=.;
IF BBhh5 in (1 2 3 4) THEN HA1Ep=BBhh5; ELSE HA1Ep=.;
IF BBhh6 in (1 2 3 4) THEN HA1Fp=BBhh6; ELSE HA1Fp=.;
IF BBhh7 in (1 2 3 4) THEN HA1Gp=BBhh7; ELSE HA1Gp=.;

/*Calculation of total variable*/
HAp= max (HA1Ap, HA1Bp, HA1Cp, HA1Dp, HA1Ep, HA1Fp, HA1Gp);

/*Creating a recoded variable for dissemination(Verteilung): HAc*/
IF HAp=1 THEN HAc=1;/*None/Keine*/
IF HAp=2 THEN HAc=2;/*Moderate/Mittelgradig*/
IF HAp=3 OR HAp=4 THEN HAc=3;/*Severe/Schwerwiegend*/

BBhhlimit_k=HAc;
IF age<55 THEN BBhhlimit_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), A. Difficulty in preparing meals/Schwierigkeiten Mahlzeiten zu bereiten: HA1Ad*/
IF BBhh1=1 THEN HA1Ad=1;/*None/Keine*/
IF BBhh1=2 THEN HA1Ad=2;/*Moderate/Mittelgradig*/
IF BBhh1=3 OR BBhh1=4 THEN HA1Ad=3;/*Severe/Schwerwiegend*/

BBhhlimitA_k=HA1Ad;
IF age<55 THEN BBhhlimitA_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), B. Difficulty in using the telephone/Schwierigkeiten das Telefon zu benutzen: HA1Bd*/
IF BBhh2=1 THEN HA1Bd=1;/*None/Keine*/
IF BBhh2=2 THEN HA1Bd=2;/*Moderate/Mittelgradig*/
IF BBhh2=3 OR BBhh2=4 THEN HA1Bd=3;/*Severe/Schwerwiegend*/

BBhhlimitB_k=HA1Bd;
IF age<55 THEN BBhhlimitB_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), C. Difficulty in shopping/Schwierigkeiten Einkäufe zu erledigen: HA1Cd*/
IF BBhh3=1 THEN HA1Cd=1;/*None/Keine*/
IF BBhh3=2 THEN HA1Cd=2;/*Moderate/Mittelgradig*/
IF BBhh3=3 OR BBhh3=4 THEN HA1Cd=3;/*Severe/Schwerwiegend*/

BBhhlimitC_k=HA1Cd;
IF age<55 THEN BBhhlimitC_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), D. Difficulty in managing medication/Schwierigkeiten Medikamenteneinnahme zu organisieren: HA1Dd*/
IF BBhh4=1 THEN HA1Dd=1;/*None/Keine*/
IF BBhh4=2 THEN HA1Dd=2;/*Moderate/Mittelgradig*/
IF BBhh4=3 OR BBhh4=4 THEN HA1Dd=3;/*Severe/Schwerwiegend*/

BBhhlimitD_k=HA1Dd;
IF age<55 THEN BBhhlimitD_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), E. Difficulty in light housework/Schwierigkeiten leichte Hausarbeit zu erledigen: HA1Ed*/
IF BBhh5=1 THEN HA1Ed=1;/*None/Keine*/
IF BBhh5=2 THEN HA1Ed=2;/*Moderate/Mittelgradig*/
IF BBhh5=3 OR BBhh5=4 THEN HA1Ed=3;/*Severe/Schwerwiegend*/

BBhhlimitE_k=HA1Ed;
IF age<55 THEN BBhhlimitE_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), F. Difficulty in occasional heavy housework/Schwierigkeiten gelegentlich schwere Hausarbeit zu erledigen: HA1Fd*/
IF BBhh6=1 THEN HA1Fd=1;/*None/Keine*/
IF BBhh6=2 THEN HA1Fd=2;/*Moderate/Mittelgradig*/
IF BBhh6=3 OR BBhh6=4 THEN HA1Fd=3;/*Severe/Schwerwiegend*/

BBhhlimitF_k=HA1Fd;
IF age<55 THEN BBhhlimitF_k=.N;/*Nicht erhoben Age<55*/

/*Creating a recoded variable for dissemination(Verteilung), G. Difficulty in taking care of finances and everyday administrative tasks/Schwierigkeiten Verwaltungsangelegenheiten zu erledigen: HA1Gd*/
IF BBhh7=1 THEN HA1Gd=1;/*None/Keine*/
IF BBhh7=2 THEN HA1Gd=2;/*Moderate/Mittelgradig*/
IF BBhh7=3 OR BBhh7=4 THEN HA1Gd=3;/*Severe/Schwerwiegend*/

BBhhlimitG_k=HA1Gd;
IF age<55 THEN BBhhlimitG_k=.N;/*Nicht erhoben Age<55*/

/*Calculation of total variable*/
TLp= max (PCp, HAp);

/*Creating a recoded variable for dissemination(Verteilung): TLc*/
IF TLp=1 THEN TLc=1;/*None/Keine*/
IF TLp=2 THEN TLc=2;/*Moderate/Mittelgradig*/
IF TLp=3 OR TLp=4 THEN TLc=3;/*Severe/Schwerwiegend*/

BBtotallimit_k=TLc;
IF age<55 THEN BBtotallimit_k=.N;/*Nicht erhoben Age<55*/

/*Calculation of total variable*/
TALp= max (PChelp, HAhelp);

/*Creating a derived variable: TAhelp*/
IF TALp=1 THEN TAhelp=1;/*No need for assistance*/
IF TALp=2 THEN TAhelp=2;/*Get enough assistance*/
IF TALp=3 THEN TAhelp=3;/*Lack of assistance*/

BBtotalhilfe_k=TAhelp;
IF age<55 THEN BBtotalhilfe_k=.N;/*Nicht erhoben Age<55*/
IF BBhhhilfe1=.F AND BBhilfe1=.F THEN Bbtotalhilfe_k=.F;

/*Medizinische Versorgung nach Unfall (3 Kat.)*/
*UVarztF_k entspricht EHIS-Var AC2;
IF UVarztF in (1 2) THEN UVarztF_k=1;/*admission to a hospital or any other health facility*/
IF UVarztF=3 THEN UVarztF_k=2;/*a doctor or nurse*/ 
IF UVarztF=4 THEN UVarztF_k=3;/*no intervention was needed*/ 
IF UVarztF in (.E .F .K .W) THEN UVarztF_k=UVarztF;/**/


/*31.05.2021 SSB: Anpassung Transfer-Tool*/

IF ((Uvartverkehr NE 1) and (Uvarthaus NE 1) and (Uvartfreiz NE 1)) then UVarztF_k =.F;

IF UVsturz12z>=2 THEN UVsturz12mf=1;
IF UVsturz12=2 OR UVsturz12z=1 THEN UVsturz12mf=2;
IF UVsturz12z in (.N .E .W) THEN UVsturz12mf=.;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq1B = PKphq1 +1;
IF  PKphq1 in (.K .W) THEN PKphq1B=PKphq1;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq2B = PKphq2 +1;
IF  PKphq2 in (.K .W) THEN PKphq2B=PKphq2;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq3B = PKphq3 +1;
IF  PKphq3 in (.K .W) THEN PKphq3B=PKphq3;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq4B = PKphq4 +1;
IF  PKphq4 in (.K .W) THEN PKphq4B=PKphq4;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq5B = PKphq5 +1;
IF  PKphq5 in (.K .W) THEN PKphq5B=PKphq5;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq6B = PKphq6 +1;
IF  PKphq6 in (.K .W) THEN PKphq6B=PKphq6;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq7B = PKphq7 +1;
IF  PKphq7 in (.K .W) THEN PKphq7B=PKphq7;

/*analog GEDA14 neue EHIS-Variablen generiert: PkphqxB mit EHIS-Kodierungen von 1 bis 4 (die in der SynopseDB angelegten Variablen PKphqx sind umkodiert worden in 0 bis 3)*/
Pkphq8B = PKphq8 +1;
IF  PKphq8 in (.K .W) THEN PKphq8B=PKphq8;

/*Calculation of derived variables for each variable MH1x, where x = A, B, C, D, E, F, G, H => hierzu die genVars PkphqxB mit EHIS-Kodierungen von 1 bis 4 einsetzen*/
/*MH1A/PKphq1B: Wenig Interesse oder Freude an Ihren Tätigkeiten*/
IF  PKphq1B in (3 4) THEN MHAdum = 1; 
IF  PKphq1B in (1 2) THEN MHAdum = 0; 
IF  PKphq1B in (.K .W) THEN MHAdum = PKphq1B; 

/*MH1B/PKphq2B: Niedergeschlagenheit, Schwermut oder Hoffnungslosigkeit*/
IF  PKphq2B in (3 4) THEN MHBdum = 1; 
IF  PKphq2B in (1 2) THEN MHBdum = 0; 
IF  PKphq2B in (.K .W) THEN MHBdum = PKphq2B; 

/*MH1C/PKphq3B: Schwierigkeiten, ein- oder durchzuschlafen oder vermehrter Schlaf*/
IF  PKphq3B in (3 4) THEN MHCdum = 1; 
IF  PKphq3B in (1 2) THEN MHCdum = 0; 
IF  PKphq3B in (.K .W) THEN MHCdum = PKphq3B; 

/*MH1D/PKphq4B: Müdigkeit oder Gefühl, keine Energie zu haben*/
IF  PKphq4B in (3 4) THEN MHDdum = 1; 
IF  PKphq4B in (1 2) THEN MHDdum = 0; 
IF  PKphq4B in (.K .W) THEN MHDdum = PKphq4B; 

/*MH1E/PKphq5B: Verminderter Appetit oder übermäßiges Bedürfnis zu essen*/
IF  PKphq5B in (3 4) THEN MHEdum = 1; 
IF  PKphq5B in (1 2) THEN MHEdum = 0; 
IF  PKphq5B in (.K .W) THEN MHEdum = PKphq5B; 

/*MH1F/PKphq6B: Schlechte Meinung von sich selbst; ein Gefühl, ein Versager zu sein oder die Familie enttäuscht zu haben*/
IF  PKphq6B in (3 4) THEN MHFdum = 1; 
IF  PKphq6B in (1 2) THEN MHFdum = 0; 
IF  PKphq6B in (.K .W) THEN MHFdum = PKphq6B; 

/*MH1G/PKphq7B: Schwierigkeiten, sich auf etwas zu konzentrieren, z.B. beim Zeitungslesen oder Fernsehen*/
IF  PKphq7B in (3 4) THEN MHGdum = 1; 
IF  PKphq7B in (1 2) THEN MHGdum = 0; 
IF  PKphq7B in (.K .W) THEN MHGdum = PKphq7B; 

/*MH1H/PKphq8B: Sprache verlangsamt oder zappelig, ruhelos, stärkerer Bewegungsdrang*/
IF  PKphq8B in (3 4) THEN MHHdum = 1; 
IF  PKphq8B in (1 2) THEN MHHdum = 0; 
IF  PKphq8B in (.K .W) THEN MHHdum = PKphq8B;

/*Calculation of the summary score: Mhbsum*/
IF (PKphq1B not in (.K .W) AND PKphq2B not in (.K .W) AND PKphq3B not in (.K .W) AND PKphq4B not in (.K .W) AND PKphq5B not in (.K .W) AND PKphq6B not in (.K .W) AND PKphq7B not in (.K .W) AND PKphq8B not in (.K .W)) THEN DO; Mhbsum = sum(MHAdum, MHBdum, MHCdum, MHDdum, MHEdum, MHFdum, MHGdum, MHHdum); END;

/*Calculation of the final variable: MHcds/PKphqsum_k1*/
IF (MHAdum=1 OR MHBdum=1) AND (MHbsum>=5) THEN MHcds=1;/*Major depressive symptoms/Bedeutende depressive Symptome*/
IF (MHAdum=1 OR MHBdum=1) AND (MHbsum in (2 3 4)) THEN MHcds=2;/*Other depressive symptoms/Andere depressive Symptome*/
IF (MHcds NE 1 AND MHcds NE 2) AND (PKphq1B not in (.K .W) AND PKphq2B not in (.K .W) AND PKphq3B not in (.K .W) AND PKphq4B not in (.K .W) AND PKphq5B not in (.K .W) AND PKphq6B not in (.K .W) AND PKphq7B not in (.K .W) AND PKphq8B not in (.K .W)) THEN MHcds=0;/*No depressive symptoms/Keine depressiven Symptome*/

Pkphqsum_k1=MHcds;

/*Calculation of summary score: MHsum => hierzu die genVars PkphqxB mit EHIS-Kodierungen von 1 bis 4 einsetzen*/
IF (PKphq1B not in (.K .W) AND PKphq2B not in (.K .W) AND PKphq3B not in (.K .W) AND PKphq4B not in (.K .W) AND PKphq5B not in (.K .W) AND PKphq6B not in (.K .W) AND PKphq7B not in (.K .W) AND PKphq8B not in (.K .W)) THEN DO; MHsum = sum (PKphq1B, PKphq2B, PKphq3B, PKphq4B, PKphq5B, PKphq6B, PKphq7B, PKphq8B); END;

/*Recoding the score: Mhsumd/PKphqsum_k2*/
IF Mhsum>=8 AND Mhsum<=12 THEN Mhsumd=1;/*None or minimum/Keine oder minimale depressive Symptome*/
IF Mhsum>=13 AND Mhsum<=17 THEN Mhsumd=2;/*Mild/Leichte depressive Symptome*/
IF Mhsum>=18 AND Mhsum<=22 THEN Mhsumd=3;/*Moderate/Moderate depressive Symptome*/
IF Mhsum>=23 AND Mhsum<=27 THEN Mhsumd=4;/*Moderately severe/Mittelschwere depressive Symptome*/
IF Mhsum>=28 AND Mhsum<=32 THEN Mhsumd=5;/*Severe/Schwere depressive Symptome*/

Pkphqsum_k2=Mhsumd;

/*analog GEDA14: PKphq8sum - Skalensummenwert Depressivität, Kodierungen 0 bis 3*/
PKphq8sum = sum (PKphq1, PKphq2, PKphq3, PKphq4, PKphq5, PKphq6, PKphq7, PKphq8);
